import "./App.css";
import React from "react";
import Table from "./Table";

class App extends React.Component {
  state = {
    students: [
      {
        name: "Harry Potter",
        house: "Gryffindor",
      },
      {
        name: "Ron Weasley",
        house: "Gryffindor",
      },
      {
        name: "Draco Malfoy",
        house: "Slytherin",
      },
      {
        name: "Cho Chang",
        house: "Ravenclaw",
      },
    ],
  };

  removeStudent(index) {
    const { students } = this.state;

    this.setState({
      students: students.filter((student, i) => {
        return i !== index;
      }),
    });
  }

  render() {
    const { students } = this.state;

    return (
      <div className="container">
        <Table studentData={students} removeStudent={this.removeStudent} />
      </div>
    );
  }
}

export default App;
